/**拖拽表格 */
import React from 'react';
import 'antd/dist/antd.css';
import './index.css';
import { Table, Input, InputNumber, Popconfirm } from 'antd';




export interface CAFormOfTableProps {

}

export interface CAFormOfTableState {
  data: any
}


class EditableTable extends React.Component<CAFormOfTableProps, CAFormOfTableState>{
  private columns;
  constructor(props: CAFormOfTableProps) {
    super(props);
    this.state = { data };
    this.columns = [
      {
        title: 'name11',
        dataIndex: 'name',
        width: '25%',
        editable: true,
      },
      {
        title: 'age',
        dataIndex: 'age',
        width: '15%',
        editable: true,
        render: (text, record) => {
          return <InputNumber
            value={text}
            onChange={(e) => {
              this.save('age', e, record, record.key)
            }}></InputNumber>
        }
      },
      {
        title: 'address',
        dataIndex: 'address',
        width: '40%',
        editable: true,
      }
    ];
  }

  render() {


    return (
      <div>123</div>
    );
  }
}

export default EditableTable;
